#pragma once
#include <vector>
#include <string>
#include <fstream>

using namespace std;
struct BlockInfo //For creating maps in C++
{
	int blocktype; //Defined for the type of block; 1 = Wall; 0 = Nothing
	int blockcolor; //Defined for the color
};
struct ObjInfo { //For creating objects in C++
	//Todo create an ID system
	int ObjType; //If the object is a sign or something
	int ObjLoc; //Map Location
	COORD ObjCurrent; //Current Object Coords
	COORD ObjOld; //Old Object Coords
	int ObjCollsion; //If the object has collsion
	double ObjNum; //Optional double Value assigned
	string Objstring; //Optional Text Value assigned
	int ObjID;
}; 
struct InvInfo {
	string Name;
	int ID;
};
vector<ObjInfo> Objmap; 

vector<InvInfo> Inventory;
/*Object Keycode

Ignore second, third, and fourth letters, those correspond to the location. Still important but not what we are about to discuss.

First letter = Type
fourth, Fifth and Sixth are optional variables. fourth is for object collsionfifth is an integer, and sixth is a character array.

We will refer to these as Var A, B, C, and D

1 Sign:
Int controls nothing
Char controls readout

2 AI:
Int controls nothing (yet)
Char controls readout

3 TP:
Int controls where to tp too using complicated equations
Char controls readout

4 Item:
Int controls what ID the items uses (for the command Use later)
Char controls readout and what to call the item if picked up
*/
void LoadTestGame() {
	Objmap.push_back({ 1, 0, {5, 10}, {5, 10}, 1, 0.0, "Test Sign", 0 });
	Objmap.push_back({ 2, 0, {19, 11}, {19, 11}, 0, 0.0, "Test AI", 1});
	Objmap.push_back({ 3, 0, {2, 7}, {2, 7}, 0, 6435.01, "Entrance", 2 });
	Objmap.push_back({ 3, 1, {1, 7}, {1, 7}, 0, 6420, "Exit", 3 });
	Objmap.push_back({ 4, 0, {24, 9}, {24, 9}, 0, 1, "Random Item", 4});
	Objmap.push_back({ 4, 0, {16, 11}, {16, 11}, 0, 1, "Random Something", 5});
	Objmap.push_back({ 2, 0, {45, 5}, {45, 5}, 0, 0.0, "Test AI2", 6 });
}




