vector<BlockInfo> XVect(60, {0, 0}); //X Coord for 60 Vectors
vector<vector<BlockInfo>> MapLoad(15, XVect);

void LoadMap() {
	BlockInfo TempBlockInfo; //Temp to store values
	int StringPoint; //Pointer to a char in the string
	string Line; //To getline
	string PartLine; //To get part of a line
	ifstream gamefile; //To load file
	gamefile.open("game.txt");
	for (int y = 1; y < 16; y++) {
		StringPoint = 0;
		getline(gamefile, Line);
		for (int x = 0; x < 60; x++) {
			while (Line[StringPoint] != ',') { //Copy until we hit a , which indicates a change to the color
				PartLine = PartLine + Line[StringPoint];
				StringPoint++;
			}
			TempBlockInfo.blocktype = stoi(PartLine);
			StringPoint++;
			PartLine = "";
				while (Line[StringPoint] != ',') { //Copy until we hit a , which indicates a change to the next block
					PartLine = PartLine + Line[StringPoint];
					StringPoint++;
				}
				TempBlockInfo.blockcolor = std::stoul(PartLine, nullptr, 16);
				MapLoad[y - 1][x] = TempBlockInfo; //Copies the info we got and restarts
				StringPoint++;
				PartLine = "";
		}
	}
}
